        private static void Start()
        {
            Console.ForegroundColor = ConsoleColor.DarkRed;
            Console.WriteLine("Type Your Secret Message To Send To The Overseer Agent X:");
            Console.ResetColor();
            string UserString = Console.ReadLine();


            Console.ForegroundColor = ConsoleColor.DarkRed;
            Console.Write("How many keys would you like to shift your message? It must be a whole number Agent!\n" +
                "(Do a negative number to shift backwards.)\n");
            Console.ResetColor();
            int key = Convert.ToInt32(Console.ReadLine());
            Console.WriteLine("\n");

            Console.ForegroundColor = ConsoleColor.DarkYellow;
            Console.WriteLine("Your Encrypted Message Agent X:");
            Console.ResetColor();

            string cipherText = Encipher(UserString, key);
            Console.WriteLine(cipherText);
            Console.Write("\n");
           
            Console.ForegroundColor = ConsoleColor.DarkYellow;
            Console.WriteLine("Your Decoded Message Agent X:");
            Console.ResetColor();

            string t = Decipher(cipherText, key);
            Console.WriteLine(t);
            Console.Write("\n");

            WriteLine("Press any key to end transmission...");
            ReadKey();
            Console.ResetColor();
            Console.Clear();
           
        }
